<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');/** * Login * @package Login * @author Atul * @category CI_Model API */class Task_model extends CI_Model{    function __construct()    {        parent::__construct();    }        //fetch books    function get_tasks($limit, $start, $st = NULL) {        if ($st == "NULL")            $st = "";        $sql = "select * from " . TABLES::$CRON_TEXTMSG . " where user_id = " . $this->user_id . " limit " . $start . ", " . $limit;        $query = $this->db->query($sql);        return $query->result_array();    }    function get_task_count($st = NULL) {        if ($st == "NIL")            $st = "";        $sql = "select * from " . TABLES::$MST_AUTO_RESPONDER . " where user_id = " . $this->user_id . "";        $query = $this->db->query($sql);        return $query->num_rows();    }}