<?phpif (!defined('BASEPATH'))    exit('No direct script access allowed');/** * Login * @package Login * @author Atul * @category CI_Model API */class Responder_model extends CI_Model{    function __construct()    {        parent::__construct();    }    /**     * Add admin user     * @param array $data     *   * @return inetger id     */    public function getReponderData($id)    {        $this->db->select('a.*,b.*');        $this->db->from(TABLES::$MST_AUTO_RESPONDER . ' as a');        $this->db->join(TABLES::$TRANS_AUTO_RESPONDER . ' as b', 'a.id = b.responder_id', 'inner');        $this->db->where('a.id', $id);        $query = $this->db->get();        echo $this->db->last_query();        return $query->result_array();    }     //fetch books    function get_autodata($limit, $start, $st = NULL) {        if ($st == "NULL")            $st = "";        $sql = "select * from " . TABLES::$MST_AUTO_RESPONDER . " where user_id = " . $this->user_id . " limit " . $start . ", " . $limit;        $query = $this->db->query($sql);        return $query->result_array();    }    function get_contact_count($st = NULL) {        if ($st == "NIL")            $st = "";        $sql = "select * from " . TABLES::$MST_AUTO_RESPONDER . " where user_id = " . $this->user_id . "";        $query = $this->db->query($sql);        return $query->num_rows();    }}